# coding:utf-8
import requests
from bs4 import BeautifulSoup


def solve(N):
    return factordb(N)


# factordb.com
def factordb(N):
    res = requests.get('http://factordb.com/index.php?query=' + str(N))
    soup = BeautifulSoup(res.text, 'lxml')
    factor = []
    for i in soup.find_all('font'):
        if i.string and '.' not in str(i.string) and '*' not in str(i.string):
            factor.append(int(i.string))
    while N in factor:
        factor.remove(N)
    if len(factor) is 2:
        return factor
    else:
        return


def isqrt(n):
    x = n
    y = (x + n // x) // 2
    while y < x:
        x = y
        y = (x + n // x) // 2
    return x


# |p-q|较小
def p_q_2_close(n):
    a = isqrt(n)
    b2 = a * a - n
    b = isqrt(n)
    count = 0
    while b * b != b2:
        a = a + 1
        b2 = a * a - n
        b = isqrt(b2)
        count += 1
    p = a + b
    q = a - b
    assert n == p * q
    return p, q


